import { Component, AfterViewInit, ElementRef, OnInit, ViewChild } from '@angular/core';
import { DrawingService } from './drawing.service';

@Component({
  selector: 'app-drawing',
  templateUrl: './drawing.component.html',
  styleUrls: ['./drawing.component.scss']
})
export class DrawingComponent implements OnInit, AfterViewInit {
  @ViewChild('base') baseCanvasRef: ElementRef;
  @ViewChild('drawing') drawingCanvasRef: ElementRef;

  constructor(private drawingService: DrawingService) { 
    
  }

  ngOnInit() {
  }

  ngAfterViewInit() {
    this.setupBaseDiagram();

    this.setupDrawingArea();
  }

  private setupBaseDiagram() {
    const ctx = this.baseCanvasRef.nativeElement.getContext('2d');
    // Draw components
    this.drawingService.drawImage(ctx, 50, 50, "data:image/svg+xml;base64,");
    this.drawingService.drawImage(ctx, 50, 850, "data:image/svg+xml;base64,");
    this.drawingService.drawImage(ctx, 1050, 50, "data:image/svg+xml;base64,");
    this.drawingService.drawImage(ctx, 1050, 850, "data:image/svg+xml;base64,");

    // Draw lines
    this.drawingService.drawVerticalLine(ctx, 100, 850, 75);
    this.drawingService.drawHorizontalLine(ctx, 100, 1050, 75);
    this.drawingService.drawVerticalLine(ctx, 100, 850, 1075);
    this.drawingService.drawHorizontalLine(ctx, 100, 1050, 875);
  }

  private setupDrawingArea() {
    const ctx = this.drawingCanvasRef.nativeElement.getContext('2d');

    this.drawingService.drawHorizontalLine(ctx, 75, 1075, 475);
  }

  onClick(e: MouseEvent) {
    if (e.layerX <= 195 || e.layerX >= 905 || e.layerY <= 195 || e.layerY >= 705) {
      return;
    }

    this.drawParts(e.layerX, e.layerY);
  }

  private drawParts(x: number, y: number) {
    const ctx = this.drawingCanvasRef.nativeElement.getContext('2d');

    // Remove Node
    this.drawingService.clearLine(ctx, x - 120, 475, 145);

    // Draw Parts
    this.drawingService.drawVerticalLine(ctx, 75, 875, x + 25);
    this.drawingService.drawImage(ctx, x, 50, "data:image/svg+xml;base64,");
    this.drawingService.drawImage(ctx, x, 850, "data:image/svg+xml;base64,");

    // Draw Node
    this.drawingService.drawHorizontalLine(ctx, x - 120, x + 25, y + 25);
    this.drawingService.drawVerticalLine(ctx, y + 25, 475, x - 120);
    this.drawingService.drawImage(ctx, x, y, "data:image/svg+xml;base64,");

  }
}
